import Deserts from "../Deserts"
import Mains from "./Mains"
import Starters from "./Starters"
import SubMenu from "./SubMenu"

let starters=[ {image:"/Images/Vada.jpg", title:"Vada Sambhar",price:10, Id:1}, {image:"/Images/vegetable-samosa.webp", title:"Vegetable-samosa",price:10, Id:2},{image:"/Images/VadaPav.jpg", title:"VadaPav",price:10, Id:3},{image:"/Images/Onionpakoda.jpg", title:"Onion Pakodas",price:15, Id:4}]
let mains=[{image:"/Images/IndianThali.JPG", title:"Indian Thali",price:20}, {image:"/Images/Butterchicken.jpg", title:"Butter Chicken",price:10},{image:"/Images/PalakPaneer.jpg", title:"Palak Paneer",price:10},{image:"/Images/Chickenbiryani.png", title:"Chicken Biryani",price:10}]
let dess=[{image:"/Images/Rasmalai.jpg", title:"Rasmalai",price:10}, {image:"/Images/Gulabjamun.JPG", title:"Gulab Jamun",price:10},{image:"/Images/Malpua.jpg", title:"Malpua",price:10},{image:"/Images/gajarhalwa.jpg", title:"Gajar Halwa",price:10}]

function Menu(props) {
  const [cart, setCart] = useState([]);
  const addToCart = (el) => {
    setCart([...cart, el]);
};
const cartItems = cart.map((el) => (
  <div key={el.id}>
    {`${el.name}: $${el.price}`}
    <input type="submit" value="remove" onClick={() => removeFromCart(el)} />
  </div>
));
  return (
    
    <>
    
    <h2>Welcome to Maharaja Kitchen</h2>
   
    <SubMenu items={starters} heading="Starters"/>
    <SubMenu items={mains} heading="Mains" />
    <SubMenu items={dess} heading="Deserts" />
    <div>CART</div>
      <div>{cartItems}</div>
    </>
  

  
    
  )
}



export default Menu
