import React, { useState } from "react"
import  './Bookmovie.css';
function Bookmovie(){
   const [num, setNum] = useState(0)
   
   const incNum = () =>{

      setNum(num +1);

   }  
   const decNum = () =>{
      if (num > 0){
      setNum(num -1);
      }
      else {
         alert('Sorry,Zero limit')}
         setNum(0)

   };
      return(
      <>
        <div className="Main_div">
           <div className="Center_div">
            Use the Add and Remove buttons to enter the number of tickets you want to buy.  Tickets will be sent through email.
              <h1>{num}</h1>
                  <div className="btn_div">
                <button onClick={incNum}>Add</button>
              <button onClick={decNum}>Remove</button>
           </div>
          </div>
       </div>
      </>
    )
}

export default Bookmovie
